package ufc.ck017.mmjc.activationRecords.mips;import ufc.ck017.mmjc.activationRecords.frame.Access;import ufc.ck017.mmjc.translate.tree.BINOP;import ufc.ck017.mmjc.translate.tree.CONST;import ufc.ck017.mmjc.translate.tree.Exp;import ufc.ck017.mmjc.translate.tree.MEM;public class InFrame extends Access {	int offset;	InFrame(int o) {		offset = o;	}	public Exp exp(Exp fp) {		return new MEM(new BINOP(BINOP.PLUS, fp, new CONST(offset)));	}	public String toString() {		Integer offset = new Integer(this.offset);		return offset.toString();	}}