package ufc.ck017.mmjc.instructionSelection.assem;import ufc.ck017.mmjc.activationRecords.temp.Temp;import ufc.ck017.mmjc.activationRecords.temp.Label;import java.util.List;public class OPER extends Instr {	public OPER(String assem, Temp[] dst, Temp[] src, List<Label> jumps) {		this.assem = assem;		use = src;		def = dst;		this.jumps = jumps;	}		public OPER(String assem, Temp[] dst, Temp[] src) {		this.assem = assem;		use = src;		def = dst;	}	@Override	public Temp[] def() {		return def;	}	@Override	public Targets jumps() {		return (Targets) jumps;	}	@Override	public Temp[] use() {		return use;	}}